import React, { Component } from "react";
import { BrowserRouter as Router, Link, Route, Switch } from "react-router-dom";
import ReactPaginate from "react-paginate";

class ItemPages extends Component {
  constructor() {
    super();
    this.state = { clients: [], currentPage: 0 };
  }

  componentDidMount() {
    //{this.props.name}
    // fetch("/itempage/electric_guitar")
    fetch(`/itempage/${this.props.name}`)
      .then((response) => response.json())
      //データがゼロの時は、エラーを出す
      .then((data) => this.setState({ clients: data }))
      .then(() => {
        let updatedClients = [...this.state.clients];
        console.log("updatedClients:" + JSON.stringify(updatedClients));
        console.log("{this.props.name}:" + this.props.name);
      });
  }

  render() {
    const copiedNumbers = this.state.clients;

    const usersPerPage = 12;
    const pagesVisited = this.state.currentPage * usersPerPage;
    let displayItems = copiedNumbers
      .slice(pagesVisited, pagesVisited + usersPerPage)
      .map((num) => {
        return (
          <li>
            <Link
              to={`/items/${this.props.name}/${num.id}/`}
              style={{ textDecoration: "none" }}
            >
              <ul className="each-item">
                <img
                  className="inst-pic"
                  src={`http://localhost:8080/${num.imageName}/${num.id}/image/download`}
                />
                <li className="brand-name">{num.brandName}</li>
                <li className="item-name">
                  <p>{num.itemName}</p>
                </li>
                <li className="price">￥{num.price}</li>
              </ul>
            </Link>
          </li>
        );
      });

    const pageCount = Math.ceil(this.state.clients.length / usersPerPage);

    const changePage = ({ selected }) => {
      // setPageNumber(selected);
      this.setState({ currentPage: selected });
    };

    let numObj = [];
    // let displayUsers2 = displayItems;
    while (displayItems.length > 0) {
      let fourItems = displayItems.splice(0, 4);
      numObj.push(<ul className="item-page-list">{fourItems}</ul>);
    }

    return (
      <div className="body">
        <div className="container">
          <div className="item-page-lists">{numObj}</div>
          <div>{this.props.name}</div>

          <ReactPaginate
            previousLabel={"Previous"}
            nextLabel={"Next"}
            pageCount={pageCount}
            onPageChange={changePage}
            containerClassName={"paginationBttns"}
            previousLinkClassName={"pre"}
            nextLinkClassName={"next"}
            disabledClassName={"page"}
            activeClassName={"pagenationActive"}
          />

          <footer>
            <small>Copyright © 2020-2021, Instrumental Page.</small>
          </footer>
        </div>
      </div>
    );
  }
}

export default ItemPages;
